import React, { useState } from "react";
import { motion } from "framer-motion";
import RegisterForm from "./RegisterForm";
import axios from "axios";

export default function LoginForm(props) {
	const [isUser, setIsUser] = useState({ username: "", password: "" });

	const handleSubmit = (event) => {
		event.preventDefault();

		axios.post("https://wassup-backend.herokuapp.com/api/v1/auth/login", {
			email: String,
			password: String,
		});
	};

	return (
		<motion.div className="login-wrapper">
			<motion.div>
				<h2>Please Login</h2>
			</motion.div>
			<hr />
			<div>
				<form onSubmit={handleSubmit}>
					<input type="text" placeholder="Username" value="email"></input>
					<input name="username" type="password" placeholder="Password"></input>
					<button
						// onClick={setIsUser({ username: "test", password: "test" })}
						type="submit">
						Login
					</button>
				</form>
				<span>
					Don't have an account?
					<button onClick={props.hasAccount}>Register Here</button>
				</span>
			</div>
		</motion.div>
	);
}
